import { useState } from "react";
import './Contact.css'; // Make sure this file is created with the necessary styles

const Contact = () => {
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        subject: '',
        message: ''
    });
    
    const [isSubmitted, setIsSubmitted] = useState(false);
    const [isLoading, setIsLoading] = useState(false); // For loading state

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Simple validation
        if (!formData.name || !formData.phone || !formData.subject || !formData.message) {
            alert('Please fill in all fields.');
            return;
        }

        setIsLoading(true); // Set loading state to true

        try {
            const response = await fetch('http://localhost:5000/contact', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(formData),
            });
            
            const data = await response.json();

            if (response.ok) {
                setIsSubmitted(true);
                setFormData({ name: '', phone: '', subject: '', message: '' }); // Reset form data
                setTimeout(() => {
                    setIsSubmitted(false); // Hide after 3 seconds
                }, 3000);
            } else {
                alert(data.message || 'Failed to send message.');
            }
        } catch (error) {
            console.error('Error:', error);
            alert('An error occurred while sending your message. Please try again later.');
        } finally {
            setIsLoading(false); // Reset loading state
        }
    };

    return (
        <section className="contact">
            <div className="page-top">
                <div className="container">
                    <div className="row">
                        <div className="col-lg-12">
                            <h1 className="page-title">Contact</h1>
                            <h2 className="page-description">Contact</h2>
                        </div>
                    </div>
                </div>
            </div>
            <div className="page-content">
                <div className="container">
                    <form onSubmit={handleSubmit}>
                        <div className="row">
                            <div className="col-lg-12">
                                <div className="row">
                                    <div className="col-lg-6">
                                        <label>Name Surname</label>
                                        <input 
                                            type="text" 
                                            className="inp-contact" 
                                            name="name" 
                                            value={formData.name} 
                                            onChange={handleChange} 
                                        />
                                    </div>
                                    <div className="col-lg-6">
                                        <label>Phone</label>
                                        <input 
                                            type="text" 
                                            className="inp-contact" 
                                            name="phone" 
                                            value={formData.phone} 
                                            onChange={handleChange} 
                                        />
                                    </div>
                                    <div className="col-lg-12">
                                        <label>Subject</label>
                                        <input 
                                            type="text" 
                                            className="inp-contact" 
                                            name="subject" 
                                            value={formData.subject} 
                                            onChange={handleChange} 
                                        />
                                    </div>
                                    <div className="col-lg-12">
                                        <label>Message</label>
                                        <textarea 
                                            className="ta-contact" 
                                            rows="4" 
                                            name="message" 
                                            value={formData.message} 
                                            onChange={handleChange} 
                                        ></textarea>
                                    </div>
                                    <div className="col-lg-12">
                                        <button className="btn-contact" type="submit" disabled={isLoading}>
                                            {isLoading ? 'Sending...' : 'Send Message'}
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </form>
                </div>
            </div>

            {isSubmitted && (
                <div className="modal animate">
                    <div className="modal-content">
                        <h2>Message Sent Successfully!</h2>
                    </div>
                </div>
            )}
        </section>
    );
};

export default Contact;
